import axios from 'axios'
import keycloak from '../keycloak'

export const PacsService = {
    findFullImageById,
    authToken,
}
function findFullImageById(id){
    authToken()
    var res = instance.get(`/api/fullimage/${id}`)
    return res
}

// -- Axios https://github.com/axios/axios#config-defaults
const instance =  axios.create({
    headers: {
        'Content-Type': 'image/png'
    }
})

instance.interceptors.request.use(request => {
    console.log('Starting Request', JSON.stringify(request, null, 2))
    return request
})

instance.interceptors.response.use(response => {
    console.log('Response:', JSON.stringify(response, null, 2))
    console.log(response)
    return response

})

// -- Helper functions
export function authToken() {
    // set default header to be sent with every request
    instance.defaults.headers.common.Authorization = keycloak.token;
}
